<?php defined( '_JEXEC' ) or die( 'Restricted access' );jimport( 'joomla.application.component.view');class F2CSearchViewQuerys extends JView{		function display($tpl = null)	{		$rows =& $this->get('data');		$option = JRequest::getVar('option');				$this->assignRef('rows', $rows);		$this->assignRef('option', $option);		$this->addToolBar();		parent::display($tpl);	}	/**	 * Setting the toolbar	 */	protected function addToolBar() 	{		JToolBarHelper::title(JText::_('Querys'), 'query');		JToolBarHelper::deleteListX();		JToolBarHelper::editListX();		JToolBarHelper::publishList();		JToolBarHelper::unpublishList();		JToolBarHelper::addNew();	}}